css:
@media (min-width: 992px){
.top-bar {
    background: #ffed00;
}
  .container {
    max-width: 1250px;
}
    .box-center img, .banners-grid:nth-child(3) .container .col-lg-6 img {transition:0.5s all;}

    .box-center img:hover, .banners-grid:nth-child(3) .container .col-lg-6 img:hover {
        transition:0.5s all;
        transform: scale(1.03);
    }

    .custom-html .html-clearfix {
    margin: 0;
}
.header {
    background: #008c2c;
}
.header__category {
    color: #ffffff;
}

.container {
    /* max-width: 1250px !important; */
}

.banner-central {
    background: url(../img/uploads/bg-center.jpg) no-repeat center top;
    min-height: 291px;
    padding-top: 20px;
}
.banner-tarja {
    justify-content: center;
    display: flex;
    flex-direction: column;
    align-items: center;
    align-content: center;
    text-align: center;
}
.banner-tarja .img-info {}
.banner-tarja .span {}
.custom-html .container:first-child {
    max-width: 100%;
}
.custom-html {
    margin: 0;
    padding: 0;
}
.custom-html .container .banner-central .container {
    max-width: 1250px !important;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    align-content: center;
    font-family: ui-monospace;
}

.banner-tarja div:last-child{
    flex-direction: column;
    display: flex;
    font-family: 'Ubuntu', sans-serif;
    color: #151515;
    font-size: 16px;
    line-height: 20px;
    font-weight: 500;
    margin-top: 10px;
}
.banner-slider--full-width {
    max-height: calc(160rem*var(--banner-aspect-ratio));
    max-width: 160rem;
}
.custom-html .container:first-child {
    max-width: 100%;
    padding: 0;
}

.banners-grid:nth-child(3) {
    margin: -50px 0 100px;
}
.banners-grid:nth-child(3) .container {
    max-width: 1600px;
    justify-content: center;
    align-items: center;
    align-content: center;
    display: flex;
}

.banners-grid:nth-child(3) .container .col-lg-6:nth-child(1) {
    flex: 0 0 78%;
    max-width: 78%;
}
.banners-grid:nth-child(3) .container .col-lg-6:nth-child(2) {
    flex: 0 0 22%;
    max-width: 22%;
}
.banners-grid:nth-child(3) .container .col-lg-6 img {
    border-radius: 30px;
}

body {
    font-family: 'Ubuntu', sans-serif !important;
}

.products-carousel {
    padding: 0 0rem;
}

.product-item {
    height: 100%;
    border: solid 1px #e3e3e3;
    border-radius: 30px;
    box-shadow: 1px 1px 7px rgb(0 0 0 / 20%);
}

a:hover {
    text-decoration: none;
}

.product-card__name {
    --max-lines: 3;
    color: #000;
    font-size: 14px;
    line-height: 20px;
    text-align: center;
    padding: 0 10px;
    justify-content: center;
    align-items: center;
    align-content: center;
    display: flex;
    min-height: 83px;
}

.product-card__prices {
    color: #000;
    font-size: 16px;
    padding: 0 30px;
    display: flex;
    justify-content: center;
    align-items: center;
}

    .product-card__prices span {
    font-size: 17px;
    letter-spacing: -1px;
}
    .prices__compare {}

    .prices__value {
    font-weight: 700;
    font-size: 18px;
    color: #000000;
}
    .prices__installments {
    font-weight: 400 !important;
    line-height: 20px;
    text-align: center;
}

    .prices__compare {
}

    .prices__discount span {
    font-size: 27px;
    font-weight: 700;
    color: #096727;
    }

.prices__discount {
    margin: 0px 0;
}
    .product-item .btn-primary {
    border-radius: 25px;
}

  .custom-html:nth-child(6) {
    display: flex;
    justify-content: center;
    align-items: center;
    align-content: center;
    text-align: center;
}

.custom-html:nth-child(6) .box-center {
    gap: 10px;
    display: flex;
    justify-content: center;
    margin: 10px 0;
}

.custom-html:nth-child(6) .box-center {
    gap: 10px;
    display: flex;
    justify-content: center;
    margin: 10px 0;
}

    .blog .card {
    border: solid 1px #e3e3e3;
    border-radius: 30px;
    box-shadow: 1px 1px 7px rgb(0 0 0 / 20%);
    min-height: 410px;
}
    .blog .card {}

    .blog .card img {
    max-height: 257px;
    border-radius: 16px;
}
    .card-text {
    font-size: 12px;
    text-align: justify;
}

    .blog .container {
    max-width: 1310px;
}

    .card-title {
    font-size: 15px;
    line-height: 20px;
}


.products-carousel__title {background: url(../img/uploads/tarja.png) no-repeat left center;text-align: left;margin: 20px 0;}

.products-carousel__title:before {
    display: none;
}

.products-carousel__title a {
    background: transparent;
    letter-spacing: -1px;
    color: #fff000;
    font-size: 26px;
    display: flex;
}

.products-carousel__title a:after {
    content:
    "Uma seleção de produtos que não pode faltar, para uma saúde melhor";
    font-weight: 300;
    color: #fff;
    font-size: 13px;
    letter-spacing: 0px;
    max-width: 270px;
    margin: 0 10px;
    border-left: solid 1px #fff;
    padding-left: 11px;
}

    .prices__discount .prices__discount-label {font-size: 14px; color:#000;display: block;letter-spacing: 0px;}

}
