.banners-grid:nth-child(3) {
    margin: -50px 0 100px;
}
.banners-grid:nth-child(3) .container {
    max-width: 1600px;
    justify-content: center;
    align-items: center;
    align-content: center;
    display: flex;
}

.banners-grid:nth-child(3) .container .col-lg-6:nth-child(1) {
    flex: 0 0 78%;
