}
.banners-grid:nth-child(3) .container .col-lg-6:nth-child(2) {
    flex: 0 0 22%;
    max-width: 22%;
}
.banners-grid:nth-child(3) .container .col-lg-6 img {
    border-radius: 30px;
}

.banner-slider--full-width {
    max-height: calc(160rem*var(--banner-aspect-ratio));
    max-width: 160rem;
}

body {
    font-family: 'Ubuntu', sans-serif !important;
}

.products-carousel {
    padding: 0 0rem;
}

.product-item {
    height: 100%;
    border: solid 1px #e3e3e3;
    border-radius: 30px;
    box-shadow: 1px 1px 7px rgb(0 0 0 / 20%);
}

a:hover {
    text-decoration: none;
}

.product-card__name {
    --max-lines: 3;
    color: #000;
    font-size: 14px;
    line-height: 20px;
    text-align: center;
    padding: 0 10px;
    justify-content: center;
    align-items: center;
    align-content: center;
    display: flex;
    min-height: 83px;
}

.product-card__prices {
    color: #000;
    font-size: 16px;
    padding: 0 30px;
    display: flex;
    justify-content: center;
    align-items: center;
}

    .product-card__prices span {
    font-size: 17px;
    letter-spacing: -1px;
}
    .prices__compare {}

    .prices__value {
    font-weight: 700;
    font-size: 18px;
    color: #000000;
}
    .prices__installments {
    font-weight: 400 !important;
    line-height: 20px;
    text-align: center;
}

    .prices__compare {
}

    .prices__discount span {
    font-size: 27px;
    font-weight: 700;
    color: #096727;
    }

.prices__discount {
    margin: 0px 0;
}
    .product-item .btn-primary {
    border-radius: 25px;
}

    .prices__discount .prices__discount-label {font-size: 14px; color:#000;display: block;letter-spacing: 0px;}

}
